// üê∂ 'mocks' permet de simuler le backend netflix avec MSW,
// ne t'en preocupe pas pour le moment
import './mocks'
// üê∂ 'authNetflix' notre outils founis par les devs back qui permet de se connecter
// ü§ñ import * as authNetflix from './utils/authNetflixProvider'
import {createTheme, ThemeProvider} from '@mui/material/styles'
// üê∂ 'AuthApp' contient tout ce qu'il y avait avant dans 'App'
// il est import√© est retourn√©, nous avons donc le meme comportement qu'avant
import {AuthApp} from 'AuthApp'
// üê∂ 'UnauthApp' qui contiendra le contenu de l'application en mode non connect√©

const theme = createTheme({
  palette: {
    type: 'dark',
    primary: {
      main: '#E50914',
    },
    secondary: {
      main: '#E50914',
    },
  },
})

// üê∂ on veut afficher soit <AuthApp /> soit <UnauthApp />
// en fonction d'un user connect√© ou non
function App() {
  // üê∂ cr√©√© un state 'authUser' qui contiendra le 'user' connect√©

  // üê∂ cr√©√© une fonction 'login' avec un param√®tre 'data' (objet avec 'username' et 'password')
  // cette fonction appellera ensuite la fonction 'login' de 'authNetflix' avec data en parametre
  // met ensuite √† jour 'authUser' avec le resultat de la fonction

  // üê∂ cr√©√© une fonction 'register' avec un param√®tre 'data' (objet avec 'username' et 'password')
  // cette fonction appellera ensuite la fonction 'register' de  'authNetflix' avec data en parametre
  // met ensuite √† jour 'authUser' avec le resultat de la fonction

  // üê∂ cr√©√© une fonction 'logout' qui appelle la fonction 'logout' de  'authNetflix'
  // et met √† jour 'authUser' √† null
  // note : pour tester la deconnexion on poura cliquer sur le logo avatar (haut droite de la Appbar)
  return (
    <ThemeProvider theme={theme}>
      {/* üê∂ conditionne l'affichage de <AuthApp /> <UnauthApp /> en fonction de 'authUser'   */}
      {/* üê∂ passe le prop 'logout' √†  <AuthApp />    */}
      <AuthApp />
      {/* üê∂ passe les prop 'login' et 'register'  √†  <UnauthApp />    */}
    </ThemeProvider>
  )
}

export {App}
