import * as React from 'react'
import './mocks'
import * as authNetflix from './utils/authNetflixProvider'
import {createTheme, ThemeProvider} from '@mui/material/styles'
import {AuthApp} from 'AuthApp'
import {UnauthApp} from 'UnauthApp'
import {useFetchData} from 'utils/hooks'
const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#E50914',
    },
    secondary: {
      main: '#E50914',
    },
  },
})

function App() {
  // const [authUser, setAuthUser] = React.useState(null)
  const [data, execute, setData] = useFetchData()
  const login = data => authNetflix.login(data).then(user => setData(user))
  const register = data =>
    authNetflix.register(data).then(user => setData(user))
  const logout = () => {
    authNetflix.logout()
    setData(null)
  }
  return (
    <ThemeProvider theme={theme}>
      {authUser ? (
        <AuthApp logout={logout} />
      ) : (
        <UnauthApp login={login} register={register} />
      )}
    </ThemeProvider>
  )
}

export {App}
