import {NetflixApp} from 'components/NetflixApp'
import {ThemeProvider} from '@mui/styles'
import {createTheme} from '@mui/material/styles'
import {ErrorBoundary} from 'react-error-boundary'
import ErrorFallback from './components/ErrorFallback'
// ğŸ¶ importe le composant 'Error404' depuis '/components/Error404'
// ğŸ¶ importe le composant 'NetflixMovies', 'NetflixSeries','NetflixNews', 'NetflixById'
// ğŸ¶ importe -> import {BrowserRouter as Router, Routes, Route} from 'react-router-dom'

const theme = createTheme({
  palette: {
    type: 'dark',
    primary: {
      main: '#E50914',
    },
    secondary: {
      main: '#E50914',
    },
  },
})

function App() {
  return (
    // ğŸ¶ wrappe toute l'application avec <Router>
    <ThemeProvider theme={theme}>
      <ErrorBoundary FallbackComponent={ErrorFallback}>
        {/* 
          ğŸ¶ utilise <Routes> et  </Routes> pour determiner les routes
          nous voulons les routes avec les configurations suivantes :

          1. path '/' -> <NetflixApp />
          2. path '/tv/:tvId' -> <NetflixById />
          3. path '/tv/:movieId' -> <NetflixById />
          4. path '/series' -> <NetflixSeries />
          5. path '/movies' -> <NetflixMovies />
          6. path '/news' -> <NetflixNews />
          7. path '/*' -> <Error404 />

        */}
        <NetflixApp />
      </ErrorBoundary>
    </ThemeProvider>
  )
}

export {App}
