import React, {useEffect} from 'react'
import {NetflixAppBar} from './NetflixAppBar'
import {NetflixRow} from './NetflixRow'
import {NetFlixFooter} from './NetFlixFooter'
import axios from 'axios'
import {useState} from 'react'
// üê∂ importe 'axios'
import './Netflix.css'

// üê∂ passe en prop 'movie' qui contiendra les informations d'un film
const NetflixHeader = ({movie}) => {
  console.log(movie)
  // üê∂ La classe 'banner' contient une image fixe dans 'NetFlix.css' (background-image: url('../assets/images/banner.jpg'))
  // Tu vas devoir changer l'image (donc cette propri√©t√© CSS) pour chaque nouveau film.
  // Pour cela tu vas passer par le prop 'style' de <header> est passer l'objet avec les propi√©t√©s css.
  // Tu vas donc avoir besoin de 2 choses : 'imageUrl' : l'url de l'image et 'banner' l'objet avec les propri√©t√©s CSS
  const imageUrl = `https://image.tmdb.org/t/p/original${movie?.backdrop_path}`
  // üê∂ 1. Cr√©e une constante 'imageUrl' qui contient l'url de l'image
  //  - l'image est contenu dans 'backdrop_path' de l'objet 'movie'
  //  - la base de l'url d'image est 'https://image.tmdb.org/t/p/original'
  //
  // üê∂ 2. cr√©√© l'objet 'banner' avec les propi√©t√©s suivantes:
  const banner = {
    backgroundImage: `url('${imageUrl}')`,
    backgroundSize: 'cover',
    backgroundPosition: 'center center',
    color: 'white',
    objectFit: 'contain',
    height: '448px',
  }

  // üê∂ si 'movie' n'est pas d√©fini, retourne un fragment vide ü§ñ <></>
  if (!movie) {
    return <></>
  } else {
    // üê∂ si 'movie' est d√©fini, retourne le header
    return (
      // ‚õèÔ∏è supprime le prop 'className' et utilise le prop 'style' avec l'objet 'banner'
      <header style={banner}>
        <div className="banner__contents">
          <h1 className="banner__title">{movie?.title ?? '...'}</h1>
          <div className="banner__buttons">
            <button className="banner__button banner__buttonplay">
              Lecture
            </button>
            <button className="banner__button banner__buttonInfo">
              Ajouter √† ma liste
            </button>
          </div>
          <h1 className="synopsis">{movie?.overview ?? '...'}</h1>
        </div>
        <div className="banner--fadeBottom"></div>
      </header>
    )
  }
}

// üê∂ Tu vas devoir g√©rer le fetch de donn√©es avec axios dans 'NetflixApp'
const NetflixApp = () => {
  // üê∂ Cr√©√© un state 'headerMovie'
  const [headerMovie, setHeaderMovie] = useState()
  const defaultMovieId = 399566
  // üê∂ Cr√©√© une constante 'defaultMovieId' avec un id de film par d√©faut (399566 par exemple)
  // üê∂ utilise ta cley d'api et langue
  const apiKey = '4fc7b001e8a107fe1fddc6b41ed0f4af'
  const lang = 'fr-fr'

  // üê∂ utilise le hook 'useEffect' pour r√©cup√©rer le film sur cette url
  const url = `https://api.themoviedb.org/3/movie/${defaultMovieId}?api_key=${apiKey}&language=${lang}`
  //  avec axios :
  useEffect(() => {
    axios
      .get(url)
      .then(response => setHeaderMovie(response))
      .catch(error => console.error(error))
  }, [url])
  // axios
  //   .get(url)
  //   .then(response => console.log(response))
  //   .catch(error => console.error(error))
  // üê∂ met √† jour 'headerMovie' sur la reponse d'axios

  return (
    <div>
      <NetflixAppBar />
      {/* üê∂ passe le prop 'movie' avec la propi√©t√© 'data' de 'headerMovie'  */}
      <NetflixHeader movie={headerMovie?.data} />
      <NetflixRow wideImage={false} title="Films Netflix" />
      <NetflixRow wideImage={true} title="S√©rie Netflix" />
      <NetFlixFooter />
    </div>
  )
}
export {NetflixApp}
